import React, { useState } from 'react';
import Button from '../Button/Button';
import styled from 'styled-components';
import { saveAs } from 'file-saver';

const PredictionPage = () => {
  const [year, setYear] = useState('');
  const [predictions, setPredictions] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!year || isNaN(year) || year < 2000 || year > new Date().getFullYear()) {
      setError('Please enter a valid year between 2000 and the current year.');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/v1/predict-expenses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ year }),
      });

      if (response.ok) {
        const data = await response.json();
        setPredictions(data);
        setError('');
      } else {
        setError('Failed to fetch predictions.');
      }
    } catch (error) {
      setError('Error occurred while fetching predictions.');
    } finally {
      setLoading(false);
    }
  };

  // Function to download predictions as CSV
  const downloadCSV = () => {
    const csvContent = "data:text/csv;charset=utf-8," +
      "Month,Category,Prediction\n" +
      predictions.map(pred => `${pred.month},${pred.category},${pred.prediction}`).join("\n");
    const encodedUri = encodeURI(csvContent);
    saveAs(encodedUri, "predictions.csv");
  };

  return (
    <div>
      <h1>Expense Predictions for Year</h1>

      <PredictionPageStyled onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter year (2000 - current year)"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <div className="submit-btn">
          <Button 
            name={'Submit'}
            bPad={'.8rem 1.6rem'}
            bRad={'30px'}
            bg={'var(--color-accent)'}
            color={'#fff'}
          />
        </div>
      </PredictionPageStyled>

      {loading && <p>Loading predictions...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {predictions.length > 0 && (
        <div>
          <h2>Predictions:</h2>
          <PredictionListStyled>
            {predictions.map((prediction, index) => (
              <li key={index}>
                <strong>{prediction.month}</strong> - {prediction.category}: ${prediction.prediction.toFixed(2)}
              </li>
            ))}
          </PredictionListStyled>

          <div className="export-btn">
            <Button 
              name={'Export as CSV'}
              onClick={downloadCSV}
              bPad={'.8rem 1.6rem'}
              bRad={'30px'}
              bg={'var(--color-green)'}
              color={'#fff'}
            />
          </div>
        </div>
      )}
    </div>
  );
};

const PredictionPageStyled = styled.form`
    display: flex;
    flex-direction: column;
    gap: 2rem;
    input, textarea, select {
        font-family: inherit;
        font-size: inherit;
        outline: none;
        border: none;
        padding: .5rem 1rem;
        border-radius: 5px;
        border: 2px solid #fff;
        background: transparent;
        resize: none;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        color: rgba(34, 34, 96, 0.9);
        &::placeholder {
            color: rgba(34, 34, 96, 0.4);
        }
    }
    .submit-btn {
        button {
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            &:hover {
                background: var(--color-green) !important;
            }
        }
    }
`;

const PredictionListStyled = styled.ul`
    list-style-type: none;
    padding: 0;
    li {
        margin: 1rem 0;
        background: #f0f4f8;
        padding: 0.5rem;
        border-radius: 5px;
        box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    }
`;

export default PredictionPage;
