import React, { useState } from 'react';
import Button from '../Button/Button';
import styled from 'styled-components';
import { saveAs } from 'file-saver';

const PredictionPage = () => {
  const [year, setYear] = useState('');
  const [predictions, setPredictions] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const currentYear = new Date().getFullYear();
    
    // Allow years from 2000 up to 2100 for prediction
    if (!year || isNaN(year) || year < 2000 ) {
      setError(`Please enter a valid year : `);
      return;
    }

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/v1/predict-expenses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ year }),
      });

      if (response.ok) {
        const data = await response.json();
        setPredictions(data);
        setError('');
      } else {
        setError('Failed to fetch predictions.');
      }
    } catch (error) {
      setError('Error occurred while fetching predictions.');
    } finally {
      setLoading(false);
    }
  };

  // Function to download predictions as CSV
  const downloadCSV = () => {
    const csvContent = 
      "Month,Category,Prediction\n" +
      predictions.map(pred => `${pred.month},${pred.category},${pred.prediction}`).join("\n");

    // Create a Blob from the CSV content and use FileSaver to save it
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    saveAs(blob, "predictions.csv");
  };

  return (
    <div>
      

      <PredictionPageStyled onSubmit={handleSubmit}>
      <h1>Expense Predictions for Year</h1>
        <input
          type="text"
          placeholder="Enter year "
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <div className="submit-btn">
          <Button 
            name={'Submit'}
            bPad={'.8rem 1.6rem'}
            bRad={'30px'}
            bg={'var(--color-accent)'}
            color={'#fff'}
          />
        </div>
      </PredictionPageStyled>

      {loading && <p>Loading predictions...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {predictions.length > 0 && (
        <div>
          <h2>Predictions:</h2>
          <PredictionPageStyled>
            {predictions.map((prediction, index) => (
              <li key={index}>
                <strong>{prediction.month}</strong> - {prediction.category}: â‚¹{prediction.prediction.toFixed(2)}
              </li>
            ))}
          </PredictionPageStyled>

          <div className="export-btn">
            <Button 
              name={'Export as CSV'}
              onClick={downloadCSV}
              bPad={'.8rem 1.6rem'}
              bRad={'30px'}
              bg={'var(--color-green)'}
              color={'#fff'}
            />
          </div>
        </div>
      )}
    </div>
  );
};



const PredictionPageStyled = styled.form`
    display: flex;
    flex-direction: column;
    gap: 2rem;
    input, textarea, select {
        font-family: inherit;
        font-size: inherit;
        outline: none;
        border: none;
        padding: .5rem 1rem;
        border-radius: 5px;
        border: 2px solid #fff;
        background: transparent;
        resize: none;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        color: rgba(34, 34, 96, 0.9);
        &::placeholder {
            color: rgba(34, 34, 96, 0.4);
        }
    }
    
    h1 {
        text-align: center; /* Centers the heading */
        margin-top: 3rem;   /* Moves the heading down */
        color: rgba(34, 34, 96, 1); /* Optional: Set the color of the heading */
    }

    .submit-btn, .export-btn {
        display: flex;
        justify-content: center; /* This centers the button horizontally */
        align-items: center;
    }
    .submit-btn {
        button {
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            &:hover {
                background: var(--color-green) !important;
            }
        }
    }
    .export-btn {
        margin-top: 2rem; /* Space between prediction list and export button */
        button {
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            &:hover {
                background: var(--color-green) !important;
            }
        }
    }
`;



export default PredictionPage;
