import React, { useState, useMemo } from 'react';
import styled from "styled-components";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';  // Import Router and Routes
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import Prediction from './Components/Prediction/Prediction';  // Import the Prediction component
import { useGlobalContext } from './context/globalContext';

function App() {
  const [active, setActive] = useState(1);

  const global = useGlobalContext();
  console.log(global);

  const orbMemo = useMemo(() => {
    return <Orb />;
  }, []);

  return (
    <Router>  {/* Wrap your app in Router */}
      <AppStyled bg={bg} className="App">
        {orbMemo}
        <MainLayout>
          <Navigation active={active} setActive={setActive} />
          <main>
            <Routes>  {/* Define routes here */}
              <Route path="/" element={<Dashboard />} />  {/* Default dashboard route */}
              <Route path="/dashboard" element={<Dashboard />} />  {/* Dashboard route */}
              <Route path="/prediction" element={<Prediction />} />  {/* Prediction route */}
              <Route path="/income" element={<Income />} />  {/* Income route */}
              <Route path="/expenses" element={<Expenses />} />  {/* Expenses route */}
            </Routes>
          </main>
        </MainLayout>
      </AppStyled>
    </Router>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
