// Components/Navigation/Navigation.js

import React from 'react';
import { Link } from 'react-router-dom';  // Use React Router Link for navigation
// Components/Navigation/Navigation.js
import styled from 'styled-components';  // Add this line at the top


const Navigation = ({ active, setActive }) => {
  return (
    <nav>
      <ul>
        <li onClick={() => setActive(1)} className={active === 1 ? 'active' : ''}>
          <Link to="/dashboard">Dashboard</Link>
        </li>
        <li onClick={() => setActive(2)} className={active === 2 ? 'active' : ''}>
          <Link to="/income">Income</Link>
        </li>
        <li onClick={() => setActive(3)} className={active === 3 ? 'active' : ''}>
          <Link to="/expenses">Expenses</Link>
        </li>
        <li onClick={() => setActive(5)} className={active === 5 ? 'active' : ''}>
          <Link to="/graph">Graph</Link> {/* New Graph Link */}
        </li>
      </ul>
    </nav>
  );
};

const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;
    .user-con{
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        img{
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: .2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }
        h2{
            color: rgba(34, 34, 96, 1);
        }
        p{
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items{
        flex: 1;
        display: flex;
        flex-direction: column;
        li{
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;
            i{
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active{
        color: rgba(34, 34, 96, 1) !important;
        i{
            color: rgba(34, 34, 96, 1) !important;
        }
        &::before{
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }
`;

export default Navigation
